import React, { useState, useEffect } from 'react';
import { Button } from '../../UI/Button';
import { Link } from 'react-router-dom';
import '../Navbar/style/navbar.css';
import banner from '../image/client/banner.jpg';
<script src="https://kit.fontawesome.com/d363a096e9.js" crossorigin="anonymous"></script>
function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

  return (
    <>
      <nav className='navbar container'>
        <div className='navbar-container '>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
            AIP&T
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                HOME
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/service'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                SERVICES
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                    to='/svgmap'
                    className='nav-links'
                    onClick={closeMobileMenu}
                    >
                    OFFICES
              </Link>
            </li>

            <li className='nav-item'>
            <Link
                  to='/teamsmate'
                  className='nav-links'
                  onClick={closeMobileMenu}
                  >
                  TEAM
            </Link>
          </li>

            <li className='nav-item'>
                <Link
                    to='/news'
                    className='nav-links'
                    onClick={closeMobileMenu}
                    >
                  NEWS
                </Link>
            </li>

            <li className='nav-item'>
                <Link
                    to='/contact'
                    className='nav-links'
                    onClick={closeMobileMenu}
                    >
                    CONTACT
                </Link>
            </li>
          </ul>
        </div>
      </nav>
  
      <section  className="jumbotron-banner jumbotron-fluid">
        <img src={banner} alt=""/>
      </section>
  
    </>
  );
}

export default Navbar;
